import { useState } from 'react';
import axios from 'axios';

function GrammarChecker() {
  const [text, setText] = useState('');
  const [corrections, setCorrections] = useState([]);
  const [hoveredWord, setHoveredWord] = useState(null);

  const handleCheckGrammar = async () => {
    try {
      const response = await axios.post(
        '/check-grammar',
        { text },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      const correctionList = response.data.corrections.split('\n').map(c => {
        const [original, corrected] = c.split(' -> ');
        return { original: original?.trim(), corrected: corrected?.trim() };
      });
      setCorrections(correctionList);
    } catch (err) {
      console.error(err);
    }
  };

  const highlightText = () => {
    let highlighted = text;
    corrections.forEach(({ original, corrected }) => {
      if (original && corrected) {
        const regex = new RegExp(`\\b${original}\\b`, 'g');
        highlighted = highlighted.replace(regex, `<span class="relative text-red-500 underline cursor-pointer" onMouseEnter="window.hoveredWord='${original}'" onMouseLeave="window.hoveredWord=null">${original}<span class="absolute hidden group-hover:block bg-gray-800 text-white text-xs rounded p-1 mt-1">${corrected}</span></span>`);
      }
    });
    return highlighted;
  };

  return (
    <div className="min-h-screen flex flex-col items-center p-4 bg-gray-100">
      <h2 className="text-2xl font-bold mb-6">Grammar Checker</h2>
      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        className="w-full max-w-2xl h-40 p-4 border rounded mb-4"
        placeholder="Enter your text here..."
      />
      <button
        onClick={handleCheckGrammar}
        className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600 mb-4"
      >
        Check Grammar
      </button>
      <div
        className="w-full max-w-2xl p-4 border rounded bg-white"
        dangerouslySetInnerHTML={{ __html: highlightText() }}
      />
    </div>
  );
}

export default GrammarChecker;
