import { useState } from 'react';
import axios from 'axios';

function GrammarChecker() {
  const [text, setText] = useState('');
  const [corrections, setCorrections] = useState([]);
  const [correctedText, setCorrectedText] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleCheckGrammar = async () => {
    if (!text.trim()) {
      setError('Please enter some text to check');
      return;
    }

    setLoading(true);
    setError('');
    setCorrections([]);
    setCorrectedText('');

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please login first');
        return;
      }

      const response = await axios.post(
        'http://localhost:3500/check-grammar',
        { text },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const correctionText = response.data.corrections;
      const backendCorrectedText = response.data.correctedText;

      if (correctionText && correctionText.trim()) {
        const correctionList = correctionText.split('\n')
          .filter(line => line.trim() && line.includes(' -> '))
          .map(line => {
            const [original, corrected] = line.split(' -> ');
            return { 
              original: original?.trim(), 
              corrected: corrected?.trim() 
            };
          });
        setCorrections(correctionList);

        // Use backend corrected text if available, otherwise generate locally
        if (backendCorrectedText) {
          setCorrectedText(backendCorrectedText);
        } else {
          // Generate corrected text locally
          let corrected = text;
          correctionList.forEach(({ original, corrected: correction }) => {
            if (original && correction) {
              const regex = new RegExp(`\\b${original.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}\\b`, 'gi');
              corrected = corrected.replace(regex, correction);
            }
          });
          setCorrectedText(corrected);
        }
      } else {
        setCorrections([]);
        setCorrectedText(backendCorrectedText || text);
      }
    } catch (err) {
      console.error('Grammar check error:', err);
      if (err.response?.status === 401) {
        setError('Session expired. Please login again.');
        localStorage.removeItem('token');
        window.location.href = '/login';
      } else {
        setError('Failed to check grammar. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-4xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-800">Grammar Checker</h1>
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          >
            Logout
          </button>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <div className="mb-4">
            <label className="block text-gray-700 font-medium mb-2">
              Enter your text to check grammar and spelling:
            </label>
            <textarea
              value={text}
              onChange={(e) => setText(e.target.value)}
              className="w-full h-40 p-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
              placeholder="Type or paste your text here..."
            />
          </div>

          <button
            onClick={handleCheckGrammar}
            disabled={loading}
            className={`w-full py-3 px-6 rounded-lg font-medium transition-colors ${
              loading 
                ? 'bg-gray-400 cursor-not-allowed' 
                : 'bg-blue-500 hover:bg-blue-600 text-white'
            }`}
          >
            {loading ? 'Checking...' : 'Check Grammar & Spelling'}
          </button>

          {error && (
            <div className="mt-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
              {error}
            </div>
          )}
        </div>

        {/* Original Text (Wrong) - Red Bold */}
        {text && (
          <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4 text-red-600">
              ‚ùå Original Text (Contains Errors)
            </h2>
            <div className="p-4 border-2 border-red-300 rounded-lg bg-red-50">
              <p className="text-red-800 font-bold text-lg leading-relaxed">{text}</p>
            </div>
          </div>
        )}

        {/* Individual Corrections */}
        {corrections.length > 0 && (
          <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4 text-gray-800">
              üîß Corrections Found ({corrections.length})
            </h2>
            <div className="space-y-3">
              {corrections.map((correction, index) => (
                <div key={index} className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg border">
                  <span className="text-red-600 font-bold text-lg">{correction.original}</span>
                  <span className="text-gray-400 text-xl">‚Üí</span>
                  <span className="text-green-600 font-bold text-lg">{correction.corrected}</span>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Corrected Text (Right) - Green */}
        {correctedText && correctedText !== text && (
          <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4 text-green-600">
              ‚úÖ Corrected Text
            </h2>
            <div className="p-4 border-2 border-green-300 rounded-lg bg-green-50">
              <p className="text-green-800 font-semibold text-lg leading-relaxed">{correctedText}</p>
            </div>
            <div className="mt-4 flex space-x-4">
              <button
                onClick={() => setText(correctedText)}
                className="bg-green-500 text-white px-6 py-2 rounded-lg hover:bg-green-600 font-medium"
              >
                Use Corrected Text
              </button>
              <button
                onClick={() => {
                  navigator.clipboard.writeText(correctedText);
                  alert('Corrected text copied to clipboard!');
                }}
                className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600 font-medium"
              >
                Copy to Clipboard
              </button>
            </div>
          </div>
        )}

        {/* No Corrections Needed */}
        {correctedText && correctedText === text && corrections.length === 0 && (
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-semibold mb-4 text-green-600">
              ‚úÖ Corrected Text
            </h2>
            <div className="p-4 border-2 border-green-300 rounded-lg bg-green-50">
              <p className="text-green-800 font-semibold text-lg leading-relaxed">{correctedText}</p>
            </div>
            <p className="text-green-600 mt-3 text-lg font-medium">üéâ No corrections needed - your text is already perfect!</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default GrammarChecker;
