import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import backendImg from '../backend.png';
import heroBg from '../herobg.png';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/login', { username, password });
      localStorage.setItem('token', response.data.token);
      navigate('/grammar-check');
    } catch (err) {
      setError('Invalid credentials');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div
      className="min-h-screen flex items-center justify-center bg-cover bg-center relative"
      style={{ backgroundImage: `url(${heroBg})` }}
    >
      {/* Animated backend.png on the left */}
      <div className="hidden md:flex flex-col items-center justify-center mr-8 animate-float-spin">
        <img
          src={backendImg}
          alt="Backend Animation"
          className="w-64 h-64 object-contain drop-shadow-2xl"
        />
      </div>
      {/* Login Form */}
      <div className="bg-white bg-opacity-90 p-8 rounded-lg shadow-2xl w-full max-w-md z-10">
        <h2 className="text-3xl font-extrabold mb-6 text-center text-blue-700 drop-shadow">Login Page For Grammar Checker</h2>
        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label className="block text-gray-700">Username</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full p-2 border rounded"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700">Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-2 border rounded"
              required
            />
          </div>
          {error && <p className="text-red-500">{error}</p>}
          <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition-all duration-200">
            Login
          </button>
        </form>
        <button
          onClick={handleLogout}
          className="w-full mt-4 bg-gray-500 text-white p-2 rounded hover:bg-gray-600 transition-all duration-200"
        >
          Logout
        </button>
      </div>
      {/* Animation keyframes for floating and spinning */}
      <style>{`
        @keyframes float-spin {
          0% { transform: translateY(0) rotate(0deg); }
          50% { transform: translateY(-30px) rotate(10deg); }
          100% { transform: translateY(0) rotate(0deg); }
        }
        .animate-float-spin {
          animation: float-spin 4s ease-in-out infinite;
        }
      `}</style>
    </div>
  );
}

export default Login;
