import { useState } from 'react';
import axios from 'axios';

function GrammarChecker() {
  const [text, setText] = useState('');
  const [corrections, setCorrections] = useState([]);
  const [hoveredCorrection, setHoveredCorrection] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleCheckGrammar = async () => {
    setIsLoading(true);
    setError(null);
    
    try {
      const response = await axios.post(
        'http://localhost:5000/check-grammar',
        { text },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      
      const correctionList = response.data.corrections.split('\n')
        .map(c => {
          const parts = c.split(' -> ');
          return { 
            original: parts[0]?.trim(), 
            corrected: parts[1]?.trim() 
          };
        })
        .filter(c => c.original && c.corrected);
        
      setCorrections(correctionList);
    } catch (err) {
      console.error('Grammar check failed:', err);
      setError('Failed to check grammar. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const highlightText = () => {
    if (!corrections.length) return text;
    
    let highlighted = text;
    corrections.forEach(({ original, corrected }) => {
      const regex = new RegExp(`\\b${original}\\b`, 'g');
      highlighted = highlighted.replace(
        regex, 
        `<span class="relative text-red-500 underline cursor-pointer" 
                onMouseEnter="this.setAttribute('data-hover', 'true')" 
                onMouseLeave="this.removeAttribute('data-hover')">
          ${original}
          <span class="absolute hidden [data-hover=true]~span:block bg-gray-800 text-white text-xs rounded p-1 mt-1">
            ${corrected}
          </span>
        </span>`
      );
    });
    return highlighted;
  };

  return (
    <div className="min-h-screen flex flex-col items-center p-4 bg-gray-100">
      <h2 className="text-2xl font-bold mb-6">Grammar Checker</h2>
      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        className="w-full max-w-2xl h-40 p-4 border rounded mb-4"
        placeholder="Enter your text here..."
      />
      <button
        onClick={handleCheckGrammar}
        disabled={isLoading}
        className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600 mb-4 disabled:bg-blue-300"
      >
        {isLoading ? 'Checking...' : 'Check Grammar'}
      </button>
      
      {error && <div className="text-red-500 mb-4">{error}</div>}
      
      <div
        className="w-full max-w-2xl p-4 border rounded bg-white"
        dangerouslySetInnerHTML={{ __html: highlightText() }}
      />
    </div>
  );
}

export default GrammarChecker;
