import { useState } from 'react';
import axios from 'axios';

function GrammarChecker() {
  const [text, setText] = useState('');
  const [corrections, setCorrections] = useState([]);
  const [correctedSentence, setCorrectedSentence] = useState('');
  const [noCorrections, setNoCorrections] = useState(false);

  const handleCheckGrammar = async () => {
    try {
      const response = await axios.post(
        'http://localhost:5000/check-grammar',
        { text },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      const lines = response.data.corrections.trim().split('\n');
      if (lines[0] === 'No corrections needed') {
        setNoCorrections(true);
        setCorrections([]);
        setCorrectedSentence('');
      } else {
        setNoCorrections(false);
        setCorrectedSentence(lines[0]);
        const correctionList = lines.slice(1).map(c => {
          const [original, corrected] = c.split(' -> ');
          return { original: original?.trim(), corrected: corrected?.trim() };
        }).filter(c => c.original && c.corrected);
        setCorrections(correctionList);
      }
    } catch (err) {
      console.error(err);
    }
  };

  // Highlight incorrect words in red and show the correct word in place
  const highlightCorrections = () => {
    if (!correctedSentence) return '';
    let highlighted = correctedSentence;
    corrections.forEach(({ original, corrected }) => {
      // Replace the corrected word with a span showing the original in red and the corrected in black
      // We'll highlight the corrected word, but show the original in red as a tooltip
      const regex = new RegExp(`\\b${corrected}\\b`, 'g');
      highlighted = highlighted.replace(
        regex,
        `<span style="color: red; font-weight: bold;">${original}</span> <span style="color: black;">${corrected}</span>`
      );
    });
    return highlighted;
  };

  return (
    <div className="min-h-screen flex flex-col items-center p-4 bg-gray-100">
      <h2 className="text-2xl font-bold mb-6">Grammar Checker</h2>
      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        className="w-full max-w-2xl h-40 p-4 border rounded mb-4"
        placeholder="Enter your text here..."
      />
      <button
        onClick={handleCheckGrammar}
        className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600 mb-4"
      >
        Check Grammar
      </button>
      <div
        className="w-full max-w-2xl p-4 border rounded bg-white"
      >
        {noCorrections ? (
          <span className="text-green-600 font-semibold">No corrections needed!</span>
        ) : (
          <span dangerouslySetInnerHTML={{ __html: highlightCorrections() }} />
        )}
      </div>
    </div>
  );
}

export default GrammarChecker;
