import { useState } from 'react';
import axios from 'axios';

function GrammarChecker() {
  const [text, setText] = useState('');
  const [corrections, setCorrections] = useState([]);
  const [correctedSentence, setCorrectedSentence] = useState('');
  const [noCorrections, setNoCorrections] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleCheckGrammar = async () => {
    if (!text.trim()) {
      setError('Please enter some text to check.');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('Please log in first.');
        setLoading(false);
        return;
      }

      const response = await axios.post(
        '/check-grammar',
        { text },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Split and filter out empty lines
      const lines = response.data.corrections.split('\n').map(l => l.trim()).filter(Boolean);
      if (lines.length === 0 || lines[0] === 'No corrections needed') {
        setNoCorrections(true);
        setCorrections([]);
        setCorrectedSentence('');
      } else {
        setNoCorrections(false);
        setCorrectedSentence(lines[0]);
        const correctionList = lines.slice(1).map(c => {
          const [original, corrected] = c.split(' -> ');
          return { original: original?.trim(), corrected: corrected?.trim() };
        }).filter(c => c.original && c.corrected);
        setCorrections(correctionList);
      }
    } catch (err) {
      console.error(err);
      if (err.response?.status === 401) {
        setError('Please log in again. Your session may have expired.');
      } else if (err.response?.status === 500) {
        setError('Server error. Please try again later.');
      } else {
        setError('Failed to check grammar. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  // Highlight incorrect words in the input text (preview) in bold red
  const highlightInput = () => {
    let highlighted = text;
    corrections.forEach(({ original }) => {
      if (original) {
        // Use word boundaries to only match the word
        const regex = new RegExp(`\\b${original}\\b`, 'gi');
        highlighted = highlighted.replace(
          regex,
          `<span style="color: red; font-weight: bold;">${original}</span>`
        );
      }
    });
    return highlighted;
  };

  return (
    <div className="min-h-screen flex flex-col items-center p-4 bg-gray-100">
      <h2 className="text-2xl font-bold mb-6">Grammar Checker</h2>

      {error && (
        <div className="w-full max-w-2xl p-3 mb-4 bg-red-100 border border-red-400 text-red-700 rounded">
          {error}
        </div>
      )}

      {/* Preview with incorrect words highlighted */}
      {corrections.length > 0 && !noCorrections && (
        <div
          className="w-full max-w-2xl p-4 border rounded bg-white mb-2"
          dangerouslySetInnerHTML={{ __html: highlightInput() }}
        />
      )}

      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        className="w-full max-w-2xl h-40 p-4 border rounded mb-4"
        placeholder="Enter your text here..."
      />

      <button
        onClick={handleCheckGrammar}
        disabled={loading}
        className={`px-6 py-2 rounded mb-4 ${
          loading 
            ? 'bg-gray-400 cursor-not-allowed' 
            : 'bg-blue-500 hover:bg-blue-600'
        } text-white`}
      >
        {loading ? 'Checking...' : 'Check Grammar'}
      </button>

      <div
        className="w-full max-w-2xl p-4 border rounded bg-white min-h-[3rem]"
      >
        {noCorrections ? (
          <span className="text-green-600 font-semibold">No corrections needed!</span>
        ) : (
          <span>{correctedSentence}</span>
        )}
      </div>
    </div>
  );
}

export default GrammarChecker;
