import { useState } from 'react';
import axios from 'axios';

function GrammarChecker() {
  const [text, setText] = useState('');
  const [corrections, setCorrections] = useState([]);
  const [correctedSentence, setCorrectedSentence] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleCheckGrammar = async () => {
    if (!text.trim()) return;

    setIsLoading(true);
    try {
      const response = await axios.post(
        '/check-grammar',
        { text },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );

      // Parse the response to get corrections
      const lines = response.data.corrections.split('\n').filter(line => line.trim());
      const correctionList = lines.map(line => {
        const [original, corrected] = line.split(' -> ');
        return { original: original?.trim(), corrected: corrected?.trim() };
      }).filter(c => c.original && c.corrected);

      setCorrections(correctionList);

      // Create corrected sentence by replacing incorrect words
      let corrected = text;
      correctionList.forEach(({ original, corrected: correctWord }) => {
        const regex = new RegExp(`\\b${original}\\b`, 'gi');
        corrected = corrected.replace(regex, correctWord);
      });
      setCorrectedSentence(corrected);

    } catch (err) {
      console.error(err);
      setCorrectedSentence('Error checking grammar. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  // Highlight incorrect words for preview
  const highlightIncorrectWords = () => {
    let highlighted = text;
    corrections.forEach(({ original }) => {
      if (original) {
        const regex = new RegExp(`\\b${original}\\b`, 'gi');
        highlighted = highlighted.replace(
          regex,
          `<span style="color: red; font-weight: bold;">${original}</span>`
        );
      }
    });
    return highlighted;
  };

  return (
    <div className="min-h-screen flex flex-col items-center p-4 bg-gray-100">
      <h2 className="text-2xl font-bold mb-6">Grammar Checker</h2>

      {/* Editable textarea */}
      <div className="w-full max-w-2xl mb-4">
        <label className="block text-gray-700 mb-2">Enter your text:</label>
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          className="w-full h-40 p-4 border rounded mb-2"
          placeholder="Enter your text here..."
        />

        {/* Preview with highlighted incorrect words */}
        {corrections.length > 0 && (
          <div className="w-full p-4 border rounded bg-white">
            <label className="block text-gray-700 mb-2 text-sm">Preview (incorrect words in red):</label>
            <div
              className="w-full min-h-[2rem]"
              dangerouslySetInnerHTML={{ __html: highlightIncorrectWords() }}
              style={{ whiteSpace: 'pre-wrap' }}
            />
          </div>
        )}
      </div>

      {/* Check Grammar Button */}
      <button
        onClick={handleCheckGrammar}
        disabled={isLoading}
        className="bg-blue-500 text-white p-3 rounded hover:bg-blue-600 mb-4 disabled:bg-gray-400"
      >
        {isLoading ? 'Checking...' : 'Check Grammar'}
      </button>

      {/* Output box with corrected sentence */}
      <div className="w-full max-w-2xl">
        <label className="block text-gray-700 mb-2">Corrected Text:</label>
        <div className="w-full p-4 border rounded bg-white min-h-[3rem]">
          {correctedSentence ? (
            <span>{correctedSentence}</span>
          ) : (
            <span className="text-gray-500">Corrected text will appear here...</span>
          )}
        </div>
      </div>
    </div>
  );
}

export default GrammarChecker;
